package day03;

public class ForEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sum=0;
		for(int i=1;i<=10;i++) {
	
			
			sum+=i;
}
		
		System.out.println(sum);
	
	System.out.println("------------------------");
	
	for (int a=10;a>0;a--) {
	
		System.out.println(a);
	}
	
		
		
	
	
	}

}
